import React from 'react';
import './App.css';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import Sidebar from './Sidebar';
import CourseArea from './CourseArea';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allCourses: {},
      filteredCourses: {},
      subjects: [],
      cart:{}
    };

    // this.add = this.add.bind(this);
  }

  componentDidMount() {
    fetch('http://mysqlcs639.cs.wisc.edu:53706/api/react/classes').then(
      res => res.json()
    ).then(data => this.setState({allCourses: data, filteredCourses: data, subjects: this.getSubjects(data)}));
  }

  getSubjects(data) {
    let subjects = [];
    subjects.push("All");

    for(const course of Object.values(data)) {
      if(subjects.indexOf(course.subject) === -1)
        subjects.push(course.subject);
    }

    return subjects;
  }

  setCourses(courses) {
    this.setState({filteredCourses: courses})
  }

  // setaddedcourse(this.state.cart){
  //   this.setState({addedcourses})
  // }

  add = (course, sec_index, subsec_index) => {

    let course_selected = {
        number: course.number,
        name: course.name,
        credits: course.credits,
        subject: course.subject,
        description: course.description,
        requisites: course.requisites,
        keywords: course.keywords,
        sections: {}
      }

      console.log("cart")
      console.log(this.state.cart)
      let newcart = Object.assign({}, this.state.cart)

      if(sec_index == null){
        course_selected = course;
        // newcart[course_selected.number] = course_selected
      }
      else{
        if(subsec_index === null){
          if(!this.state.cart[course_selected.number]){
            // Object.assign(course_selected, {sectinos: {[sec_index]:course.sections[sec_index]}})
            course_selected.sectinos = {[sec_index]:course.sections[sec_index]};

            // newcart[course_selected.number] = course_selected
            // console.log(newcart[course_selected.number])
          }
          else{
            // console.log(course.sections)
            course_selected.sections[sec_index] = course.sections[sec_index]
            // newcart[course_selected.number] = course_selected
          }
        }

        else{
          let newsec = {
            number: course.sections[sec_index].number,
            instructor: course.sections[sec_index].instructor,
            location: course.sections[sec_index].location,
            time: course.sections[sec_index].time,
            subsections: {[subsec_index]: course.sections[sec_index].subsections[subsec_index]}
          }
          if(!this.state.cart[course_selected.number]){

            course_selected.sectinos = {[sec_index]: newsec}
            // newcart[course_selected.number] = course_selected
          }
          else{

            if(Object.keys(this.state.cart[course_selected.number].sections).includes(sec_index)){

              course_selected.sections[sec_index].subsections[subsec_index] = course.sections[sec_index].subsections[subsec_index]
            }
            else{
              course_selected.sections[sec_index] = newsec
            }
          }
        }  

      }

      // course_selected.sections.subsections = Object.values(course_selected.sections.subsections)
      // course_selected.sections = Object.values(course_selected.sections)
      // course_selected.sections = course_selected.sections.map( (item) => (item.subsections = Object.values(item.subsections)))
      newcart[course_selected.number] = course_selected
      console.log("newcart")
      console.log(newcart)
      this.setState({cart: newcart})

  } 

  remove = (course, sec_index, subsec_index) => {
    let newcart = Object.assign({}, this.state.cart)

      if(sec_index == null){
        delete newcart[course.number]
      }
      else{
        if(subsec_index === null){
          delete newcart[course.number].sections[sec_index]

          if(newcart[course.number].sections == null){
            delete newcart[course.number]
          }
        }
        else{
          delete newcart[course.number].sections[sec_index].subsections[subsec_index]

          if(newcart[course.number].sections[sec_index].subsections == null){
            delete newcart[course.number].sections[sec_index]
          }

          if(newcart[course.number].sections == null){
            delete newcart[course.number]
          }
        }
      }

      this.setState({cart: newcart})
  }

  setCartdata = (newcart) => {
    let Cartdata = Object.assign({}, newcart)
    for (const [key, value] in Object.entries(Cartdata)){
        value.sections = Object.values( value.sections)
        for (const [k, v] in value.sections.entries(value)){
          if(v.subsections != null){
            v.subsections = Object.values( v.subsections)}
          }
        }

    console.log(Cartdata)
    return Cartdata
    }


  render() {
    // console.log(Object.values(this.state.cart))
    // console.log(Object.keys(this.state.filteredCourses))
    return (
      <>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
          integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
          crossOrigin="anonymous"
        />



        <Tabs defaultActiveKey="search" style={{position: 'fixed', zIndex: 1, width: '100%', backgroundColor: 'white'}}>
          <Tab eventKey="search" title="Search" style={{paddingTop: '5vh'}}>
            <Sidebar setCourses={(courses) => this.setCourses(courses)} courses={this.state.allCourses} subjects={this.state.subjects}/>
            <div style={{marginLeft: '20vw'}}>
              <CourseArea data={this.state.filteredCourses} allData={this.state.allCourses} cartMode={false} onaddtocart={this.add}/>
          </div>
          </Tab>

          <Tab eventKey="cart" title="Cart" style={{paddingTop: '5vh'}}>
            <div style={{marginLeft: '5vw'}}>
            <CourseArea data={Object.values(this.state.cart)} allData={this.state.allCourses} cartMode={true} onaddtocart={this.add} onremovecart = {this.remove}/>
            </div>
          </Tab>
        </Tabs> 
      </>
    )
  }
}

export default App;
