import React from 'react'
import './App.css'
import Subsection from './Subsection.js'

function getSubsection(section) {

		if(section.subsections === ""){
			return ""
		}
		else{
			let subsec = [];
			for(const sub of section.subsections.entries()) {
				subsec.push(<Subsection subsection={sub}/>)
			}
			return subsec
		}

}



const Section = (sections) => {
	const {section} = sections
	const  {instructor, location, number, time} = section[1];
	// console.log(section[1]);
	const day = Object.keys(time);


	return(
		<div style={{margin: '5px'}}>

				{/* <h2>Sections</h2> */}



				<div>
					<h3>{number} <button>Add Section</button></h3>

				<ul>
					<li>Instructor: {instructor}</li>
					<li>Location: {location}</li>
					<li>Metting Times
					<ul>
						{day.map((k, i) => (
						<li>{k}:{time[k]}</li>))}
						</ul>
					</li>
				</ul>
				</div>
				<h3>Subsections</h3>
				<div>


					{getSubsection(section[1])}
				</div>
		</div>
	)




}

