import React from 'react';
import './App.css';
import Section from './Section'
import "./course.css"

class Course extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addSection: "",
      addSubsection: "",
      cart: []
    }
}
  
  

  getReq (arr){
    if (arr.length === 0){
      return ""
    }
    else if(arr[0][0].constructor === Array){
      
      let list=[];
    for(const item of arr[0].entries()){
      
      list.push(item.join(" OR "))
    }
    let list1 = list.map(x =>("("+x+")"))
    return list1.join(" AND ")
    }

    else{
      // console.log('before' + arr[0])
      let newarr = arr[0].map(x =>("("+x+")"))
      // console.log('after' + newarr)
      if(newarr.length > 1){
        // console.log(newarr)
        return newarr.join(" AND ")
      }
      else{
        return newarr[0]
      }
      
    }
    
  }

  getSection(course) {
    let {sections} = this.props.data;
    if(sections.length === 0){
      return ""
    }
    else{
      let secs = [];
      secs.push(<p className="sec" key={course.name}>Sections</p>)
      for(const sect of sections.entries()) {
        secs.push (
          <Section key={sect.number} section={sect} />
        )
      }
      return secs;
    }
    

    
  }


  add (course, section, subsection){
    const course_selected = this.state.filteredCourses[course];
    const course_cart = {
      number: course_selected.number,
      name: course_selected.name
    }

    if(section !== ""){
      course_cart.sec_num = section;
      course_cart.sec = course_selected.sections[section];
    }

    if(subsection !== ""){
      course_cart.subsec_num = subsection;
    }

    if(!this.state.cart.includes(course_selected)){
      this.setState( prevState => (this.cart.push(course_selected)))
    }

  }

  handleSectionClick(){
    this.setState(prevState => ({
      addSection: false,
      addSubsection: false
    }))

   
  }



  render(){
    // console.log(this.props.data.requisites);
    const course = this.props.data;
    // const {sections} = course;
    // console.log(sections)
    // const sec = Object.values(sections);
    // const sec_keys = Object.keys(sections);
    
    return (
      
      <div>
         
            <p className="head"><span>({course.number}) ({course.name}) |</span><span className="credit"> ({course.credits} Credits)   </span>
            <button className="credit"  onClick={() => {this.add(course.number, "", "")}}> Add Course</button> </p>
            <p className="subject">Subject: {course.subject}</p>
            <p className="des">{course.description}</p>
            <p className="req"><span className="req_title">Requisites: </span><span>{this.getReq(course.requisites)}</span></p>
            <p className="key"><u>Keywords: </u> <span>{course.keywords}.join(',')</span></p>
           
           {/* {
             sec.map((k, i) => (
              <div>
              <h2>Sections</h2>
              <Section section={k} />
              </div>
             )
                )
           } */}
           
           <div className="secs">
             {this.getSection(course)}
           </div>
           <br/>
           <br/> 
      </div>
    )
  
  }
}

export default Course
