import React from 'react';
import './App.css';
import Section from './Section'
import "./course.css"

class Course extends React.Component {
//   constructor(props) {
//     super(props);
//     this.array = Object.keys(props.data).map(function(k) { return props.data[k] })
// }
  
  

  getReq (arr){
    if (arr.length === 0){
      return ""
    }
    else if(arr[0][0].constructor === Array){
      
      let list=[];
    // for(var i = 0; i < arr.length; i++) {
    //   var temp = "";
    //   for(var j = 0; j < arr[i].length; j++) {
    //     if(j === arr[i].length-1){
    //       temp = "(" + temp + arr[i][j]  + ")";
    //     }
    //     else{
    //       temp = temp + arr[i][j] + " OR ";
    //     }
    //   }
    //   list.push(temp);
    //   if(i !== arr.length-1){
    //     list.push(" AND ");
    //   }
    // } 
    // return list.join("");
    // };
    // for(var i = 0; i < arr.length; i++) {
    for(const item of arr[0].entries()){
      
      list.push(item.join(" OR "))
    }
    let list1 = list.map(x =>("("+x+")"))
    return list1.join(" AND ")
    }

    else{
      console.log(arr[0])
      let newarr = arr[0].map(x =>("("+x+")"))
      if(newarr.length > 1){
        console.log(newarr)
        return newarr.join(" AND ")
      }
      else{
        return newarr[0]
      }
      
    }
    
  }

  getSection(course) {
    let secs = [];
    
    let {sections} = this.props.data;
    for(const sect of sections.entries()) {
      secs.push (
        <Section section={sect} />
      )
    }

    return secs;
  }


  render(){
    console.log(this.props.data.requisites);
    const course = this.props.data;
    // const {sections} = course;
    // console.log(sections)
    // const sec = Object.values(sections);
    // const sec_keys = Object.keys(sections);
    
    return (
      
      <div>
         
            <p class="head"><span>({course.number}) ({course.name}) |</span><span class="credit"> ({course.credits} Credits)   </span><button class="credit">Add Course</button> </p>
            <p class="subject">Subject: {course.subject}</p>
            <p class="des">{course.description}</p>
            <p class="req"><span class="req_title">Requisites: </span><span>{this.getReq(course.requisites)}</span></p>
            <p><span>Keywords: </span> <span>{course.keywords}.join(',')</span></p>
           
           {/* {
             sec.map((k, i) => (
              <div>
              <h2>Sections</h2>
              <Section section={k} />
              </div>
             )
                )
           } */}
           <div>
             {this.getSection(course)}
           </div>


    
           
        
      
    
        
      </div>
    )
  
  }
}

export default Course
