import React from 'react';
import './App.css';
import Section from './Section'

class Course extends React.Component {
  
  

  getReq = (arr) =>{
    var list=[];
    for(var i = 0; i < arr.length; i++) {
      var temp = "";
      for(var j = 0; j < arr[i].length; j++) {
        if(j === arr[i].length-1){
          temp = "(" + temp + arr[i][j]  + ")";
        }
        else{
          temp = temp + arr[i][j] + " OR ";
        }
      }
      list.push(temp);
      if(i !== arr.length-1){
        list.push(" AND ");
      }
    } 
    return list.join("");
    };


  render(){
