import React from 'react';
import './App.css';
import Section from './Section'
import "./course.css"
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';

class Course extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addSection: "",
      addSubsection: "",
      cart: []
    }
}
  
  

  getReq (arr){
    if (arr.length === 0){
      return "None"
    }
    else if(arr[0].constructor === Array){
      
      let list=[];
      for(const item of arr.values()){
        if(item.length > 1)
          list.push(item.join(" OR "))
        else
          list.push(item)
      }
      let list1 = list.map(x =>("("+x+")"))
      return list1.join(" AND ")
    }
    
  }

  getSections(data) {
		let sects = [];
		sects.push("All");
	
		for(const section of Object.values(data.sections)) {
		  if(sects.indexOf(section.number) === -1)
		  	sects.push(section.number);
		}
	
		return sects;
  }
  
  getSectionOptions(data) {
    let sects = [];
    let sectionOptions = [];

		sects.push("All");
		for(const section of Object.values(data.sections)) {
		  if(sects.indexOf(section.number) === -1)
		  	sects.push(section.number);
		}

    for(const sect of sects) {
      sectionOptions.push(<option key={sect}>{sect}</option>);
    }

    return sectionOptions;
  }
    
    getSubections(section, num) {
      let subsects = [];
      subsects.push("All");
    
      for(const subsection of Object.values(section.subsections)) {
        if(subsects.indexOf(subsection.number) === -1)
          subsects.push(subsection.number);
      }
    
      return subsects;
      }  


  


  render(){
    // console.log(this.props.data.number);
    const course = this.props.data;
    
    return (
      
      <Card style={{marginTop: '5px', marginBottom:'5px'}}>
          <Card.Body>
            <Card.Title>{course.number}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted"> {course.name} </Card.Subtitle>
            
            <Form>
            <Form.Group controlId="formCredit"  style={{float:"middle"}} >
            <Form.Label style={{width:'30px', height:'30px', borderRadius:'50%', backgroundColor: "#7FFFD4"}}>{course.credits}cr </Form.Label>
            </Form.Group>
            
              <Form.Group controlId="formSections"  style={{float:"right"}} >
                <Form.Label>Sections</Form.Label>
                <Form.Control as="select" ref={this.section}>
                  {this.getSectionOptions(course)}
                </Form.Control>
              </Form.Group>
            </Form>

              {/* <div>
         
         <p className="head"><span>({course.number}) {course.name} |</span><span className="credit"> ({course.credits} Credits)   </span>
         {this.props.cartMode ? <button onClick={() => {this.props.removecart(this.props.data, null, null );}}>Remove Course</button> : 
      <button onClick={() => {this.props.addtocart(this.props.data, null, null ) }}>Add Course</button>}  </p>
         <p className="subject">Subject: {course.subject}</p>
         <p className="des">{course.description}</p>
         <p className="req"><span className="req_title">Requisites: </span><span>{this.getReq(course.requisites)}</span></p>
         <p className="key"><u>Keywords: </u> <span> {course.keywords.join(', ')}</span></p>
         <p className="sec" key={course.name}>Sections</p>
         <div className="secs">
          <Section key={course.sections.number} data={this.props.data} addtocart={this.props.addtocart} removecart={this.props.removecart} cartMode={this.props.cartMode} />
         </div>
          
         
         <br/>
         <br/> 
        </div> */}
          </Card.Body>
      </Card>
     
     

     
    )
  
  }
}


export default Course
