import React from 'react'
import './App.css'
import Subsection from './Subsection.js'

class Section extends React.Component {
	getSections() {
		let cour_sections = [];

		for(const section of this.props.data.sections.entries()) {
			cour_sections.push (
				<div>
					<h3>{section.number} <button>Add Section</button></h3>

				<ul>
					<li>Instructor: {section.instructor}</li>
					<li>Location: {section.location}</li>
					<li>Metting Times
						<ul>
						{
							section.details.map((subitem, i) => 
						<li>{i}:{subitem}</li>
						)
						}
						</ul>
					</li>
				</ul>) 
				<div>
					<Subsection />
				</div>
				</div>
		  )
		}

		return cour_sections;
	  }

	render() {
		return (
			<div style={{margin: '5px'}}>
				<h2>Sections</h2>

				{/* {
					this.props.data.sections.map((item) =>
				<div>
					<h3>{item.number} <button>Add Section</button></h3>

				<ul>
					<li>Instructor: {item.instructor}</li>
					<li>Location: {item.location}</li>
					<li>Metting Times
						<ul>
						{
							item.details.map((subitem, i) => 
						<li>{i}:{subitem}</li>
						)
						}
						</ul>
					</li>
				</ul>) 
				<div>
					<Subsection />
				</div>
				</div>
				)
				} */}
				{this.getSections()}
			</div>
		)
	}
}

export default Section;
