//           <button className="credit"  > Add Course</button> </p>
//           <p className="subject">Subject: {courses[key].subject}</p>
//           <p className="des">{courses[key].description}</p>
//           <p className="req"><span className="req_title">Requisites: </span><span>{this.getReq(courses[key].requisites)}</span></p>
//           <p className="key"><u>Keywords: </u> <span>{courses[key].keywords}.join(',')</span></p>
         
//          {/* {
//            sec.map((k, i) => (
//             <div>
//             <h2>Sections</h2>
//             <Section section={k} />
//             </div>
//            )
//               )
//          } */}
         
//          <div className="secs">
//            {this.Section()}
//          </div>
//          <br/>
//          <br/> 
//     </div>)}
//   ))

// }
// }

export default Course
