import React from 'react'
import './App.css'
import Subsection from './Subsection.js'
import "./section.css"
import add from './App.js'

function getSubsection(course, section) {
	// console.log(section.subsections);
		if(section.subsections.length === 0){
			return ""
		}
		else{
			let subsec = [];
			subsec.push(<p className="sub_s" key={section.subsections[1].name}>Subsections</p>)

			for(const sub of section.subsections.entries()) {
				subsec.push(<Subsection key={sub.name} course={course} section={section} subsection={sub}/>)
			}
			return subsec
		}

}



const Section = (sections, course) => {
	const {section} = sections
	const  {instructor, location, number, time} = section[1];
	// console.log(section.subsections);
	const day = Object.keys(time);


	return(
		<div style={{margin: '5px'}}>

				{/* <h2>Sections</h2> */}



				<div>
					<p className="head_s">{number} <button onClick={() => {add(course, section, {})}}>Add Section</button></p>			
				<ul className=".ul1">
					<li>Instructor: {instructor}</li>
					<li>Location: {location}</li>
					<li>Metting Times
					<ul className=".ul2">
						{day.map((k, i) => (
						<li key={k}>{k}: {time[k]}</li>))}
						</ul>
					</li>
				</ul>
				</div>
				<div>


					{getSubsection(course, section[1])}
				</div>
		</div>
	)




}



