import React from 'react'
import './App.css'
import Subsection from './Subsection.js'

function getSubsection(section) {
		let subsec = [];
		for(const sub of section.subsections.entries()) {
			subsec.push(<Subsection subsection={sub}/>)
		}
		return subsec
}

const Section = (section) => {
	// const  {instructor, location, time} = section;
	const day = Object.keys(section.time);


	return(
		<div style={{margin: '5px'}}>

				{/* <h2>Sections</h2> */}



				<div>
					<h3>{section.number} <button>Add Section</button></h3>

				<ul>
					<li>Instructor: {section.instructor}</li>
					<li>Location: {section.location}</li>
					<li>Metting Times
					<ul>
						{day.map((k, i) => (
						<li>{k}:{section.time[k]}</li>))}
						</ul>
					</li>
				</ul>
				</div>
				<div>
					<h3>Subsections</h3>

					{getSubsection(section)}
				</div>
		</div>
	)




}

