import React from 'react';
import './App.css';
// import Section from './Section'
import "./course.css"

class Course extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addSection: "",
      addSubsection: "",
      cart: []
    }
}
  
  

  getReq (arr){
    if (arr.length === 0){
      return ""
    }
    else if(arr[0][0].constructor === Array){
      
      let list=[];
    for(const item of arr[0].entries()){
      
      list.push(item.join(" OR "))
    }
    let list1 = list.map(x =>("("+x+")"))
    return list1.join(" AND ")
    }

    else{
      // console.log('before' + arr[0])
      let newarr = arr[0].map(x =>("("+x+")"))
      // console.log('after' + newarr)
      if(newarr.length > 1){
        // console.log(newarr)
        return newarr.join(" AND ")
      }
      else{
        return newarr[0]
      }
      
    }
    
  }

  

  Subsection = (section, num) => {
   let sub_sections = section[num].subsections;
   
    return Object.keys(sub_sections).map((key,index) => {
      return (

		<div >
		
			{/* <h2>Sections</h2> */}
       

			
				
			<ul>
				<li className="num_s">{sub_sections[key].number}
					<button onClick={() => {
          const {addtocart} = this.props
					
					addtocart(this.props.data, num, key );
					}}>Add Subsection</button>
				</li>			
			<ul className=".ul3">
				<li>{sub_sections[key].location}</li>
				<li>Metting Times
				<ul className=".ul4">
					{Object.keys(sub_sections[key].time).map((k, i) => (
					<li key={k}>{k}: {sub_sections[key].time[k]}</li>))}
					</ul>
				</li>
			</ul>
			</ul>
		</div>)})	
	
}



Section = () => {
  let sections = this.props.data.sections;
  var s = [];
  if(sections != null ){
    if(sections[0].subsections.length !== 0){
      s.push(<p className="sub_s" key={sections[0].number}>Subsections</p>) 
    }
  
  return Object.keys(sections).map((key,index) => {
    // console.log(key, index)
    return (

  <div >
  
    {/* <h2>Sections</h2> */}

    
      
    <ul className=".ul0">
      <li className="head_s">{sections[key].number} 
        <button onClick={() => {
       const {addtocart} = this.props
       
        addtocart(this.props.data, key, null )
        }}>Add Section</button>
      </li>			
    <ul className=".ul1">
      <li>Instructor: {sections[key].instructor}</li>
      <li>Location: {sections[key].location}</li>
      <li>Metting Times
      <ul className=".ul2">
        {Object.keys(sections[key].time).map((k, i) => (
        <li key={k}>{k}: {sections[key].time[k]}</li>))}
        </ul>
      </li>
    </ul>
    </ul>
     {s}     
    <div>
       
       {this.Subsection(sections, key)}

     </div>

  </div>)})	
  }
}

  // getSection(course) {
  //   let {sections} = this.props.data;
