class SearchAndFilter {
  
  searchAndFilter(courses, search, subject, minimumCredits, maximumCredits) {

    var res = []

    for(var i=0; i < courses.length; i++){
     
      var boolean1 = (courses[i].subject.toLowerCase() === subject.toLowerCase()) || (subject == "")
      if(search == "")
        var boolean2 = true
      else{
        var filtered = courses[i].keywords.filter((str) => str.toLowerCase().includes(search.toLowerCase())) 
        boolean2 = (filtered.length !== 0)
      }
      

      if( minimumCredits == "" && maximumCredits == "")
        var boolean3 = true
      else if(minimumCredits == "" && maximumCredits != "")
        boolean3 = (courses[i].credit <= maximumCredits)
      else if(minimumCredits != "" && maximumCredits == "")
        boolean3 = (courses[i].credit >= minimumCredits)
      else
        boolean3 = (courses[i].credit >= minimumCredits && courses[i].credit <= maximumCredits)

      if(boolean1 && boolean2 && boolean3){
        res.push(courses[i])
      }

    }
    
    
    return res;
  }
}

export default SearchAndFilter;
