import React from 'react';
import './App.css';
import Section from './Section'
import "./course.css"
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import down from './arrow-down.png';

class Course extends React.Component {
  constructor(props) {
    super(props);
    // this.section = React.createRef();
    // this.subsection = React.createRef();

    this.state = {
      addSection: "",
      addSubsection: "",
      cart: [],
      sectiom:"All",
      subsection:"All",
      sec_index: 0,
      subsec_index: 0,
      showing: false
    }
}
  
  

  getReq (arr){
    if (arr.length === 0){
      return "None"
    }
    else if(arr[0].constructor === Array){
      
      let list=[];
      for(const item of arr.values()){
        if(item.length > 1)
          list.push(item.join(" OR "))
        else
          list.push(item)
      }
      let list1 = list.map(x =>("("+x+")"))
      return list1.join(" AND ")
    }
    
  }

  // getSections(data) {
	// 	let sects = [];
	// 	sects.push("All");
	
	// 	for(const section of Object.values(data.sections)) {
	// 	  if(sects.indexOf(section.number) === -1)
	// 	  	sects.push(section.number);
	// 	}
	
	// 	return sects;
  // }
  handleChangeSec(event){    this.setState({section: event.target.value})  }
  handleChangeSubsec(event){    this.setState({subsection: event.target.value})  }
  show = () =>{this.setState(prevState => ({showing: !prevState.showing}))}


  getSectionOptions(data){
    let sects = [];
    let sectionOptions = [];

		sects.push("All");
		for(const section of Object.values(data.sections)) {
		  if(sects.indexOf(section.number) === -1)
		  	sects.push(section.number);
		}

    for(const sect of sects) {
      sectionOptions.push(<option key={sect}>{sect}</option>);
    }

    return sectionOptions;
  }
  
  getSubsectionOptions(data){
    let subsects = [];
    let sec_id = 0;
    let subSectionOptions = [];
    let sectionSelection = this.state.section;

    subsects.push("All");
    if(sectionSelection !== 'All'){
      for(const [key,section] of Object.entries(data.sections)) {
        if(section.number === sectionSelection)
          sec_id = key;
      }

      for(const subsection of Object.values(data.sections[sec_id].subsections)) {
        if(subsects.indexOf(subsection.number) === -1)
          subsects.push(subsection.number);
      }

      for(const subsect of subsects) {
        subSectionOptions.push(<option key={subsect}>{subsect}</option>);
      }
    }
    else{
      subSectionOptions.push(<option key={'All'}>{'All'}</option>);
    }
  
    return subSectionOptions;
  }  

  getIndex(){
    let sectionSelection = this.state.section;
    let subsectionSelection = this.state.subsection;
    let data = this.props.data;
    
    if(sectionSelection === 'All'){
      this.setState({sec_index: null})
      this.setState({subsec_index: null})
    }
    else{
      for(const [key,section] of Object.entries(data.sections)) {
        if(section.number === sectionSelection)
        this.setState({sec_index: key});
      }

      if(subsectionSelection === 'All'){
        this.setState({subsec_index: null})
      }
      else{
        for(const [key,subsection] of Object.entries(data.sections[this.state.sec_index].subsections)) {
          if(subsection.number === subsectionSelection)
          this.setState({subsec_index: key});
        }
      }
    }
  }


  


  render(){
    // console.log(this.props.data.number);
    const course = this.props.data;
    
    return (
      <>
      <Card style={{marginTop: '5px', marginBottom:'5px'}}>
          <Card.Body>
          
         
            
          <div className = "credit">
            <span style={{width:'27px', height:'27px', borderRadius:'50%', backgroundColor: "#7FFFD4", float:"right", marginRight:"165px",}}>{course.credits}cr </span>
            </div>
          
            <Form>
            <div>
              <Form.Group controlId="formSections" onChange={() => this.handleChangeSec} style={{marginRight:"80px", float:"right"}} >
                <Form.Label>Sections</Form.Label>
                <Form.Control as="select" ref={this.section}>
                  {this.getSectionOptions(course)}
                </Form.Control>
              </Form.Group>
              </div>
              <div>
              <Form.Group controlId="formSubsections" onChange={() => this.handleChangeSubsec} style={{marginRight:"65px", float:"right"}} >
                <Form.Label>Subsections</Form.Label>
                <Form.Control as="select" ref={this.subsection}>
                  {this.getSubsectionOptions(course)}
                </Form.Control>
              </Form.Group>
              </div>
            </Form>
            <div>
            <Button variant="outline-primary" style={{float:"right"}}>Add to Cart</Button>
            </div>
            <img src={down} alt="down" style={{float:"left"}} onClick={this.show}/ >
            <Card.Title >{course.number}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted"> {course.name} </Card.Subtitle>
            {this.state.showing?  
            <div> 
              <p className="subject">Subject: {course.subject}</p>
              <p className="des">{course.description}</p>
              <p className="req"><span className="req_title">Requisites: </span><span>{this.getReq(course.requisites)}</span></p>
              <p className="key"><u>Keywords: </u> <span> {course.keywords.join(', ')}</span></p>
              <Section key={course.sections.number} data={this.props.data} />
            </div>  
            : null}
            
            
              {/* <div>
         
         <p className="head"><span>({course.number}) {course.name} |</span><span className="credit"> ({course.credits} Credits)   </span>
         {this.props.cartMode ? <button onClick={() => {this.props.removecart(this.props.data, null, null );}}>Remove Course</button> : 
      <button onClick={() => {this.props.addtocart(this.props.data, null, null ) }}>Add Course</button>}  </p>
         <p className="subject">Subject: {course.subject}</p>
         <p className="des">{course.description}</p>
         <p className="req"><span className="req_title">Requisites: </span><span>{this.getReq(course.requisites)}</span></p>
         <p className="key"><u>Keywords: </u> <span> {course.keywords.join(', ')}</span></p>
         <p className="sec" key={course.name}>Sections</p>
         <div className="secs">
          <Section key={course.sections.number} data={this.props.data} addtocart={this.props.addtocart} removecart={this.props.removecart} cartMode={this.props.cartMode} />
         </div>
          
         
         <br/>
         <br/> 
        </div> */}
          </Card.Body>
      </Card>
     </>
     

     
    )
  
  }
}


export default Course
