import React from 'react';
import './App.css';
import Section from './Section'

class Course extends React.Component {
  
  // constructor(props) {
  //   super(props);
  //   this.state = {this.props.data}
  // }

  getReq  = (arr) =>{
    var list=[];
    for(var i = 0; i < arr.length; i++) {
      var temp = "";
      for(var j = 0; j < arr[i].length; j++) {
        if(j === arr[i].length-1){
          temp = "(" + temp + arr[i][j]  + ")";
        }
        else{
          temp = temp + arr[i][j] + " OR ";
        }
      }
      list.push(temp);
      if(i !== arr.length-1){
        list.push(" AND ");
      }
    } 
    return list.join("");
    };


  render(){
    console.log(this.props.data.length);
    

    return (
      <div>
        {
					this.props.data.map((item) =>
          <div>
            <p><span>({item.num}) ({item.name}) |</span><span> ({item.name} Credits)</span></p>
            <p>Subject: {item.description}</p>
            <p><span>Requisites: </span><span>{this.getReq(this.props.data.requisites)}</span></p>
            <p><span>Keywords: </span> <span>{item.keywords.join(" ")}</span></p>
            <Section />
        </div>
        )
        }
      </div>
    )
  
  }
}

export default Course
