import React from 'react';
import './App.css';
import Section from './Section'

class Course extends React.Component {
//   constructor(props) {
//     super(props);
//     this.array = Object.keys(props.data).map(function(k) { return props.data[k] })
// }
  
  

  getReq (arr){
    let list=[];
    // for(var i = 0; i < arr.length; i++) {
    //   var temp = "";
    //   for(var j = 0; j < arr[i].length; j++) {
    //     if(j === arr[i].length-1){
    //       temp = "(" + temp + arr[i][j]  + ")";
    //     }
    //     else{
    //       temp = temp + arr[i][j] + " OR ";
    //     }
    //   }
    //   list.push(temp);
    //   if(i !== arr.length-1){
    //     list.push(" AND ");
    //   }
    // } 
    // return list.join("");
    // };
    // for(var i = 0; i < arr.length; i++) {
    for(const item of arr.entries()){
      
      list.push(item.join(" OR "))
    }
    return list.join(" AND ");
  }

  getSection(course) {
    let secs = [];
    
    let {sections} = this.props.data;
    for(const sect of Object.values(sections)) {
      secs.push (
        <Section section={Object.keys(sect)} />
      )
    }

    return secs;
  }


  render(){
    console.log(this.props.data.number);
    const course = this.props.data;
    
    
    return (
      
      <div>
         
            <p><span>({course.num}) ({course.name}) |</span><span> ({course.credicts} Credits)</span><button>Add Course</button> </p>
            <p>Subject: {course.description}</p>
            <p><span>Requisites: </span><span>{this.getReq(course.requisites)}</span></p>
            <p><span>Keywords: </span> <span>{course.keywords}.join(',')</span></p>
           <div>
           <h2>Sections</h2>
					
					  {this.getSection(course)}
           </div>
        
      
    
        
      </div>
    )
  
  }
}

export default Course
