import React from 'react';
import moment from "moment";
import { Modal, Portal, Title, Provider, Surface } from 'react-native-paper';
import {StyleSheet, Text, View, Button, TextInput, ScrollView, TouchableOpacity, FlatList } from 'react-native';
import { Dimensions } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';


class TodayView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todayExercises: [],
            todayMeals: [],
            currentDate: new Date(),
            dailyDuration: 0.0,
            dailyCalories: 0.0,
            dailyProteins: 0.0,
            dailyCarbo: 0.0,
            dailyActivity: 0,
            dailyFats: 0.0,
            dailyFoodCal: 0.0,
            accessToken: '',

        }

    }

    async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }

    getMeal(){
        let todayProteins = 0.0;
        let todayCarbo = 0.0;
        let todayFats = 0.0;
        let todayFoodCal = 0.0;
        fetch('https://mysqlcs639.cs.wisc.edu/meals/', {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res1 => res1.json())
          .then(res1 => {
            console.log(res1, "res1")
              for(const meal of res1.meals.values()){
                  if(moment(meal.date).format('DD-MM-YYYY') === moment(this.state.currentDate).format('DD-MM-YYYY')) {
                    this.setState(prevState => ({
                        todayMeals: [...prevState.todayMeals, meal] 
                      }));
                      fetch('https://mysqlcs639.cs.wisc.edu/meals/' + meal.id + '/foods', {
                        method: 'GET',
                         headers: { 'x-access-token': this.state.accessToken }
                     })
                    .then(res2 => res2.json())
                    .then(res2 => {
                        for(const food of res2.foods.values()){
                            todayProteins += food.protein;
                            todayCarbo += food.carbohydrates;
                            todayFats += food.fat;
                            todayFoodCal = food.calories;
                        }
                      this.setState({dailyProteins: todayProteins})
                      this.setState({dailyCarbo: todayCarbo})
                      this.setState({dailyFats: todayFats})
                      this.setState({dailyFoodCal: todayFoodCal})
                    });
                    }
              }
                });
    }

    displayExercises(){
        let cardList = [];
        for (let i = 0; i < this.state.todayExercises.length; i++){
        // for(const key of this.state.activityArray.keys()){
          let value = this.state.todayExercises[i];
          console.log(value, "activity info")
          console.log(value.id, "activity id")
            let currentDate = moment(value.date).format("MMMM D, YYYY");
            cardList.push(<ActivityCard key={i} name={value.name} date={currentDate}
                           duration={value.duration} calories={value.calories}
                           id={value.id} accessToken={this.state.accessToken}
                           deleteExercise = {(id)=>this.deleteExercise(id)} />)

        }
        return cardList;
    }

    getExercise(){
        let todayDuration = 0;
        let todarCalorie = 0;
        fetch('https://mysqlcs639.cs.wisc.edu/activities/', {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res => res.json())
          .then(res => {
            console.log(res, "res")
              for(const activity of res.activities.values()){
                  if(moment(activity.date).format('DD-MM-YYYY') === moment(this.state.currentDate).format('DD-MM-YYYY')) {
                    this.setState(prevState => ({
                        todayExercises: [...prevState.todayExercises, activity] 
                      }));
                      todayDuration += activity.duration;
                      todarCalorie += activity.calories;
                      this.setState({dailyActivity: this.state.todayExercises.length})
                      this.setState({dailyDuration: todayDuration})
                      this.setState({dailyCalories: todarCalorie})
              }
            }
          });
    }


   async componentDidMount() {
        await this.getStoredData("accessToken");
        await this.getStoredData("username");
        this.getExercise();
        this.getMeal();
    }

    renderItem = (data) =>{
        return(
            <View>
            <Surface style={styles.surface}>
            <View style={{ flexDirection: 'column', flexWrap: 'wrap' }}>
                <Text style={{marginLeft:10, fontWeight: "700", fontSize: 20  }}>{data.name}</Text>
                <Text style={{marginLeft:10, marginTop:10,fontWeight: "400", fontSize: 14  }}> {moment(data.date).format('DD-MM-YYYY')}</Text>
                 <Text style={{marginLeft:13, marginTop:10,  fontWeight: "400", fontSize: 16, }}>{data.duration} min</Text>
            </View>
            <View style={{ flexDirection: 'cloumn' }}>

                <Text style={{marginLeft:30, marginTop:25, fontWeight: "500", fontSize: 27  }}>{data.calories} kcal</Text>
            </View>
         </Surface>
         <View style={styles.spaceSmall} />
         </View>
        )
    }



    render() {
        return (
            <ScrollView style={styles.mainContainer} contentContainerStyle={{ flexGrow: 11, justifyContent: 'center', alignItems: "center" }}>
                <View>
                    <Title> Exercises</Title>
                    <View style={styles.spaceSmall} />
                    <FlatList
                        data={this.state.todayExercises}
                        renderItem={(data) => this.renderItem(data)}
                        keyExtractor={item => item.name}
                    />
                </View>

                <View style = { styles.container }>
         <TouchableOpacity
              style={styles.button}
              onPress={() => {
                this.props.navigation.navigate('ComparisonView', {dailyDuration: this.state.dailyDuration,
            dailyCalories: this.state.dailyCalories,
            dailyProteins: this.state.dailyProteins,
            dailyCarbo: this.state.dailyCarbo,
           dailyFats: this.state.dailyFats,
             dailyFoodCal: this.state.dailyFoodCal,})
              }}>
              <Text style={styles.buttonText}>Check actual performance</Text>
          </TouchableOpacity>          
        </View>

            </ScrollView>
        )
    }
}

const styles = StyleSheet.create({
    scrollView: {
        height: Dimensions.get('window').height
      },
      mainContainer: {
        flex: 1
      },
      scrollViewContainer: {},
      container: {
        flex: 1,
        backgroundColor: '#fff',
      },
      spaceSmall: {
        width: 20,
        height: 10,
      },
    surface: {
        flexDirection: 'row',
        padding: 10,
        height: 100,
        width: 240,
        backgroundColor:"#FAFAD2",
        // alignItems: 'center',
        // justifyContent: 'center',
        elevation: 6,
      },

      button: {
        display: 'flex',
        height: 30,
        borderRadius: 6,
        justifyContent: 'center',
        alignItems: 'center',
        width: '90%',
        backgroundColor: '#2AC062',
        shadowColor: '#2AC062',
        shadowOpacity: 0.5,
        shadowOffset: { 
          height: 10, 
          width: 0 
        },
        shadowRadius: 25,
      },

      buttonText: {
        color: '#FFFFFF',
        fontSize: 16,
      },
});

export default TodayView;
