class ExercisesView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            duration: 0.0,
            date: "",
            calories: 0.0,
            activityArray: [],
            accessToken: "",
            username: "",
            visible: false,
          }

    }

    async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }

    deleteExercise(id){
        fetch('https://mysqlcs639.cs.wisc.edu/activities/' + id, {
          method: 'DELETE',
          headers: {
            'Content-Type': 'application/json',
            'x-access-token': this.state.accessToken
          },
        })
          .then(res => res.json())
          .then(res => {
            alert("Exercise has been deleted!");

          })
          .catch(err => {
            alert("Something went wrong! ");
          });
          this.updateData();

    }

    hideModal = () => this.setState({visible: false});
    handleAddActivity(){
        this.setState({visible: true});

        // return(

        // )
    }

    getModal(){
      if(this.state.visible){
          return(
              <AddActivity visible={this.state.visible} updateData = {() =>this.updateData() } hideModal={this.hideModal} accessToken={this.props.accessToken} />
          )
      }
  }

    updateData(){
        fetch('https://mysqlcs639.cs.wisc.edu/activities/', {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res => res.json())
          .then(res => {
            // console.log(res, "res")
              this.setState({activityArray:  res.activities});
            //   for(const [key, value] of Object.entries(res)){
            //       this.setState({[key]: value})
            //   }
          //  this.getExercises();
          });
    }

    getExercises(){
      // this.updateData();
        let cardList = [];
        // console.log(this.state.activityArray, "activity")
        for (let i = 0; i < this.state.activityArray.length; i++){
        // for(const key of this.state.activityArray.keys()){
          let value = this.state.activityArray[i];
          // console.log(value, "activity info")
          // console.log(value.id, "activity id")

            cardList.push(<ActivityCard key={i} name={value.name} date={value.date}
                           duration={value.duration} calories={value.calories}
                           id={value.id} accessToken={this.state.accessToken}
                           deleteExercise = {(id)=>this.deleteExercise(id)} 
                          updateData = {() =>this.updateData() }/>)

        }
        return cardList;
    }

    async componentDidMount() {
        await this.getStoredData("accessToken");
        await this.getStoredData("username");
        // console.log(this.state.accessToken)
        this.updateData();
        this.navListener = this.props.navigation.addListener('didFocus', () => {
          this.updateData();

       });
    }

    componentWillUnmount() {

      this.navListener();
   }

    render() {
        return (
        <ScrollView style={styles.mainContainer} contentContainerStyle={{ flexGrow: 11, justifyContent: 'center', alignItems: "center" }}>
            <View style={styles.space} />
            <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
            <Icon name="walking" size={40} color="#900" style={{ marginRight: 20 }} />
          <Text style={styles.bigText}>Exercises</Text>
            </View>
            <View style={styles.spaceSmall}></View>
        <Text>Let's get to work!</Text>
        <Text>Record your exercises below.</Text>
        {/* <View style={styles.space} />
        <Button color="#942a21" style={styles.buttonInline} title="ADD ACTIVITY" onPress={() => this.handleAddActivity()} />
        <View style={styles.space} /> */}
        <FAB
               icon="plus"
               medium
              //  size={35}
               style={styles.fab}
               onPress={() => this.handleAddActivity()}
            />
            {this.getModal()}
             {/* <ActivityModal visible={this.state.visible} hideModal={this.hideModal} accessToken={this.state.accessToken} addmode={true} editmode={false}/> */}
        <View>{this.getExercises()}</View>
        </ScrollView>)
    }
}

const styles = StyleSheet.create({
    scrollView: {
        height: Dimensions.get('window').height
      },
      mainContainer: {
        flex: 1
      },
      scrollViewContainer: {},
      container: {
        flex: 1,
        backgroundColor: '#fff',
      },
      bigText: {
        fontSize: 32,
        fontWeight: "700",
        marginBottom: 5
      },
      spaceSmall: {
        width: 20,
        height: 10,
      },
      space: {
        width: 20,
        height: 20,
      },
      buttonInline: {
        display: "flex"
      },

      fab: {
        alignSelf: 'flex-end',
        position: 'absolute',
        top: 55,
        right: 15,
        backgroundColor: '#900',
     },

});

export default ExercisesView;
