import React from 'react';
import moment from "moment";
import { Modal, Portal, Title, Provider, Surface,  Headline  } from 'react-native-paper';
import {StyleSheet, Text, View, Button, TextInput, ScrollView, TouchableHighlight } from 'react-native';
import { Dimensions } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';


class TodayView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todayExercises: [],
            todayMeals: [],
            currentDate: new Date(),
            dailyDuration: 0.0,
            dailyCalories: 0.0,
            dailyProtein: 0.0,
            dailyCarbo: 0.0,
            dailyActivity: 0,
            dailyFats: 0.0,
            dailyFoodCal: 0.0,
            accessToken: '',
            activityArray:[],

        }

    }

    async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }

    getMeal(){
        let todayProteins = 0.0;
        let todayCarbo = 0.0;
        let todayFats = 0.0;
        let todayFoodCal = 0.0;
        fetch('https://mysqlcs639.cs.wisc.edu/meals/', {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res1 => res1.json())
          .then(res1 => {
            // console.log(res1, "res1")
              for(const meal of res1.meals.values()){
                  if(moment(meal.date).format('DD-MM-YYYY') === moment(this.state.currentDate).format('DD-MM-YYYY')) {
                    this.setState(prevState => ({
                        todayMeals: [...prevState.todayMeals, meal] 
                      }));
                      fetch('https://mysqlcs639.cs.wisc.edu/meals/' + meal.id + '/foods', {
                        method: 'GET',
                         headers: { 'x-access-token': this.state.accessToken }
                     })
                    .then(res2 => res2.json())
                    .then(res2 => {
                        for(const food of res2.foods.values()){
                            todayProteins += food.protein;
                            todayCarbo += food.carbohydrates;
                            todayFats += food.fat;
                            todayFoodCal = food.calories;
                        }
                      this.setState({dailyProtein: todayProteins})
                      this.setState({dailyCarbo: todayCarbo})
                      this.setState({dailyFats: todayFats})
                      this.setState({dailyFoodCal: todayFoodCal})
                    });
                    }
              }
                });
    }

    displayExercises(){
        let cardList = [];
        for (let i = 0; i < this.state.todayExercises.length; i++){
        // for(const key of this.state.activityArray.keys()){
          let value = this.state.todayExercises[i];
          // console.log(value, "activity info")
          // console.log(value.id, "activity id")
            let currentDate = moment(value.date).format("MMMM D, YYYY");
            cardList.push(<ActivityCard key={i} name={value.name} date={currentDate}
                           duration={value.duration} calories={value.calories}
                           id={value.id} accessToken={this.state.accessToken}
                           deleteExercise = {(id)=>this.deleteExercise(id)} />)

        }
        return cardList;
    }

    // getExercise1(){
    //     let todayDuration = 0;
    //     let todarCalorie = 0;
    //     fetch('https://mysqlcs639.cs.wisc.edu/activities/', {
    //       method: 'GET',
    //       headers: { 'x-access-token': this.state.accessToken }
    //     })
    //       .then(res => res.json())
    //       .then(res => {
    //         // console.log(res, "res in today")
    //           for(const activity of res.activities.values()){
    //               if(moment(activity.date).format("MMM Do YY") === moment(this.state.currentDate).format("MMM Do YY")) {
    //                 this.setState(prevState => ({
    //                     todayExercises: [...prevState.todayExercises, activity] 
    //                   }));
    //                   todayDuration += activity.duration;
    //                   todarCalorie += activity.calories;
    //                   this.setState({dailyActivity: this.state.todayExercises.length})
    //                   this.setState({dailyDuration: todayDuration})
    //                   this.setState({dailyCalories: todarCalorie})
    //           }
    //         }
    //         console.log(this.state.todayExercises, "today exercise")
    //       });
    // }

    getExercise(){
      let todayDuration = 0;
      let todarCalorie = 0;
      this.getStoredData("activityArray");

      let activities = JSON.parse(this.state.activityArray)
      console.log(activities, "today activities")
          // console.log(res, "res in today")
            for(const activity of activities.values()){
                if(moment(activity.date).format("MMM Do YY") === moment(this.state.currentDate).format("MMM Do YY")) {
                  this.setState(prevState => ({
                      todayExercises: [...prevState.todayExercises, activity] 
                    }));
                    todayDuration += activity.duration;
                    todarCalorie += activity.calories;
                    this.setState({dailyActivity: this.state.todayExercises.length})
                    this.setState({dailyDuration: todayDuration})
                    this.setState({dailyCalories: todarCalorie})
            }
          }
          console.log(this.state.todayExercises, "today exercise")

  }


   async componentDidMount() {
        await this.getStoredData("accessToken");
        await this.getStoredData("username");
        this.getExercise();
        this.getMeal();
        console.log(this.state.dailyDuration, "this.state.dailyDuration")
        console.log(this.state.dailyCalories, "this.state.dailyCalories")
        console.log(this.state.dailyCarbo, "this.state.dailyCarbo")
        console.log(this.state.dailyProtein, "this.state.dailyProtein")
    }

    renderItem = (item) =>{
        return(
            <View key={item.name}>
            <Surface style={styles.surface}>
            <View style={{ flexDirection: 'column', flexWrap: 'wrap' }}>
                <Text style={{marginLeft:10, fontWeight: "700", fontSize: 20 }}>{item.name}</Text>
                 <Text style={{marginLeft:10,  fontWeight: "400", fontSize: 16, }}>{item.duration} min</Text>
            </View>
            <View style={{ flexDirection: 'column' }}>

                <Text style={{marginLeft:30, fontWeight: "500", fontSize: 27  }}>{item.calories} kcal</Text>
            </View>
         </Surface>
         <View style={styles.spaceSmall} />
         </View>
        )
    }

    displayExercises(){
      let cardList = [];
      // console.log(this.state.activityArray, "activity")
      for (let i = 0; i < this.state.todayExercises.length; i++){
      // for(const key of this.state.activityArray.keys()){
        let value = this.state.todayExercises[i];
        // console.log(value, "activity info")
        // console.log(value.id, "activity id")
          let currentDate = moment(value.date).format('MMMM Do YYYY');
          cardList.push( this.renderItem(value))

      }
      return cardList;
  }


    render() {
        return (
            <ScrollView style={styles.mainContainer} contentContainerStyle={{ flexGrow: 11, justifyContent: 'center', alignItems: "center" }}>

              <Headline style = {{fontWeight: '600', color:'#900'} }>{moment(new Date()).format('YYYY/MM/DD, h:mm:ss a')}</Headline>
              <View style={styles.space} />

                <View>
                    <Title> Exercises</Title>
                    <View style={styles.spaceSmall} />
                   {this.displayExercises()}
                </View>

         <TouchableHighlight
          style={styles.openButton}
              onPress={() => {
                this.props.navigation.navigate('Comparison', {dailyDuration: this.state.dailyDuration,
            dailyCalories: this.state.dailyCalories,
            dailyProtein: this.state.dailyProtein,
            dailyCarbo: this.state.dailyCarbo,
           dailyFats: this.state.dailyFats,
             dailyFoodCal: this.state.dailyFoodCal,})
              }}>
               <Text style={styles.textStyle}>Check performance</Text>
        </TouchableHighlight>



            </ScrollView>
        )
    }
}

const styles = StyleSheet.create({
    scrollView: {
        height: Dimensions.get('window').height
      },
      mainContainer: {
        flex: 1
      },
      scrollViewContainer: {},
      container: {
        flex: 1,
        backgroundColor: '#fff',
      },
      spaceSmall: {
        width: 20,
        height: 10,
      },
      space: {
        width: 20,
        height: 20,
      },
    surface: {
        flexDirection: 'row',
        padding: 10,
        height: 70,
        width: Dimensions.get('window').width *0.8,
        backgroundColor:"#FAFAD2",
