import * as React from 'react';
import {  Portal, Provider } from 'react-native-paper';
// import timePicker from "./timePicker";
import { StyleSheet, Text, View, Button, TextInput, Dimensions , Modal,} from 'react-native';
import TimePicker from './TimePicker';
import moment from "moment";
// import { Overlay } from 'react-native-elements';

class ActivityModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          name: "",
          duration: 0.0,
          date:  this.props.date,
          calories: 0.0,
          id: '',
          accessToken: '',
          modalVisible: this.props.visible,
        }
      }

      async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }

      componentDidMount() {
          // console.log(this.props.accessToken)
          // console.log(this.props.id, "id")
        //  fetch('https://mysqlcs639.cs.wisc.edu/activities/' + this.props.id, {
        //     method: 'GET',
        //     headers: { 'x-access-token': this.props.accessToken }
        //   })
        //     .then(res => res.json())
        //     .then(res => {
        //       console.log(res.date, "res.date") 
        //       this.setState({
        //         name: res.name,
        //         duration: res.duration,
        //         date: res.date,
        //         calories: res.calories,
        //         modalVisible: false,
        //         setdate: '',
        //         settime: '',
        //       }),
        //       console.log(this.state.date, "fetch date") 
        //     })
        //     console.log(this.state.date, "editmode date");
            this.setState({
              name: this.props.name,
              duration: this.props.duration,
              date:  this.props.date,
              calories: this.props.calories,
              id: this.props.id,
              accessToken: this.props.accessToken,
            })
            console.log(this.props.duration)
        }
      

      handleSaveExercise() {
        this.setState({
          duration: parseFloat(this.state.duration),
          calories: parseFloat(this.state.calories),
          
        }, 
        
        () => fetch('https://mysqlcs639.cs.wisc.edu/activities/' + this.props.id, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'x-access-token': this.props.accessToken
          },
          body: JSON.stringify({
            name: this.state.name,
              duration: this.state.duration,
              date: this.state.date,
              calories: this.state.calories,
          })
        })
          .then(res => res.json())
          .then(res => {
            this.props.updateData()
            alert("Your exercise has been updated!");
            this.props.hideModal()

          })
          .catch(err => {
            alert("Something went wrong! Verify you have filled out the fields correctly.");
          }));

          // this.setState({ modalVisible: false });
          
    
      }

      // setModalVisible = (visible) => {
      //   this.setState({ modalVisible: visible });
      // }

      getDateTime(date){
        this.setState({
          date: date// moment(date).utcOffset(6).format()
        })
      }
      // clsoe(){
      //   this.props.hideModal()
      // }

      getButton(){
        if(this.props.editmode){
          return(
          <Button color="#942a21" style={styles.buttonInline} title="SAVE EXERCISE"  onPress={() => this.handleSaveExercise()} />
          )
        }
        else{
          return(
            <Button color="#942a21" style={styles.buttonInline} title="SAVE EXERCISE"  onPress={() => this.handleAddExercise()} />
          )
        }
        
      }

      // getTimepicker(){
      //   if(this.props.editmode){
      //     return(<TimePicker date={this.state.date}  /> )
      //   }
      //   else{
      //     return(<TimePicker date={new Date()}  /> )
      //   }
        
      // }
    
//onDismiss={this.props.hideModal} contentContainerStyle={styles.mainContainer}
      render(){
        // console.log(this.props.visible, "visible");
        console.log(this.state.date, "render date");
        // if(this.props.visible){
        return (
          // <Provider>
      // <View style={styles.centeredView}>
        <Modal  visible= {this.props.visible}  >
        <View style={styles.centeredView}>
            <View style={styles.modalView}>
          <Text style={styles.bigText}>Exercise Details</Text>
          <View style={styles.space} />
        <View>
        <Text style={{ textAlignVertical: "center", fontWeight: "700" }}>Exercise Name</Text>
        </View>
        <TextInput style={styles.input}
          underlineColorAndroid="transparent"
        //   placeholder="Badger"
          placeholderTextColor="#d9bebd"
          onChangeText={(name) => this.setState({ name: name })}
          defaultValue={this.state.name}
          autoCapitalize="none" />
        <View style={styles.spaceSmall}></View>

        <View>
          <Text style={{ textAlignVertical: "center", fontWeight: "700" }}>Duration (minutes)</Text>
        </View>
        <TextInput style={styles.input}
          underlineColorAndroid="transparent"
        //   placeholder="2200"
          placeholderTextColor="#d9bebd"
          onChangeText={(duration) => this.setState({ duration: duration })}
          defaultValue={this.state.duration + ""}
          autoCapitalize="none" />
        <View style={styles.spaceSmall}></View>
        <View>
          <Text style={{ textAlignVertical: "center", fontWeight: "700" }}>Calories Burnt (cal)</Text>
        </View>
        <TextInput style={styles.input}
          underlineColorAndroid="transparent"
        //   placeholder="52"
          placeholderTextColor="#d9bebd"
          onChangeText={(calories) => this.setState({ calories: calories })}
          defaultValue={this.state.calories + ""}
          autoCapitalize="none" />
     
        <View style={styles.space} />
        <View>
          <Text style={{ textAlignVertical: "center", fontWeight: "700" }}>Exercise Data and Time</Text>    
        </View>
        <View style={styles.spaceSmall}></View>
        <TimePicker  date={this.state.date}  getDateTime={(data) =>this.getDateTime(data)}/>
        {/* getDateTime={(date) => this.getDateTime(date)} */}
        <View style={styles.space} />
        <View>
        <Text style={{ textAlignVertical: "center", fontWeight: "400" }}>Looks good! Ready to save your work?</Text>
        </View>
          
        <View style={styles.space} />  
        <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <Button color="#942a21" style={styles.buttonInline} title="SAVE EXERCISE"  onPress={() => {this.handleSaveExercise()}} />
        <View style={styles.spaceHorizontal} />
          <Button color="#a1635f" style={styles.buttonInline} title="NEVER MIND" onPress={() => this.props.hideModal()} />
        </View>
        </View>

        </View>
        </Modal>
        
      // </View>
    // </Provider>
     );}
  // );}
  // return (<View></View>)
  // }
};

const styles = StyleSheet.create({
    scrollView: {
      height: Dimensions.get('window').height
    },
    centeredView: {
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginTop: 22
    },
    mainContainer: {
      backgroundColor: 'white', 
      padding: 5,
      // height: Dimensions.get('window').height -20,
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginTop: 42,
      // marginLeft: 22,
      elevation:100,
      // width: Dimensions.get('window').width*0.9,
      // height: Dimensions.get('window').height*0.8,
    },
    modalView: {
      margin: 20,
      backgroundColor: "white",
      borderRadius: 20,
      padding: 35,
      alignItems: "center",
      shadowColor: "#000",
      shadowOffset: {
        width: 0,
        height: 2
      },
      shadowOpacity: 0.25,
      shadowRadius: 3.84,
      elevation: 5
    },
   
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    bigText: {
      fontSize: 20,
      fontWeight: "700",
      marginBottom: 5
    },
    spaceSmall: {
      width: 20,
      height: 10,
    },
    space: {
      width: 20,
      height: 20,
    },
    spaceHorizontal: {
      display: "flex",
      width: 20
    },
    buttonInline: {
      display: "flex"
    },
    input: {
      width: 200,
      padding: 10,
      margin: 5,
      height: 40,
      borderColor: '#c9392c',
      borderWidth: 1
    },
    inputInline: {
      flexDirection: "row",
      display: "flex",
      width: 200,
      padding: 10,
      margin: 5,
      height: 40,
      borderColor: '#c9392c',
      borderWidth: 1
    }
  });
  
  export default ActivityModal;
