import React from 'react';
import moment from "moment";
import { Modal, Portal, Title, Provider, Surface, DataTable } from 'react-native-paper';
import {StyleSheet, Text, View, Button, TextInput, ScrollView } from 'react-native';
import { Dimensions } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';
// import * as Progress from 'react-native-progress';
import ProgressBar from 'react-native-progress/Bar';

class ComparisonView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dailyDuration: this.props.dailyDuration,
            dailyCalories: this.props.dailyCalories,
            dailyProtein: this.props.dailyProtein,
            dailyCarbo: this.props.dailyCarbo,
            dailyFats: this.props.dailyFats,
             dailyFoodCal: this.props.dailyFoodCal,
             goalDailyCalories: 0.0,
            goalDailyProtein: 0.0,
            goalDailyCarbohydrates: 0.0,
             goalDailyFat: 0.0,
            goalDailyActivity: 0.0,
            accessToken: "",
            
        }

    }

    async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }
    

    async componentDidMount() {
        await this.getStoredData("accessToken");
        // console.log(this.state.accessToken)
        fetch('https://mysqlcs639.cs.wisc.edu/users/' + this.state.username, {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res => res.json())
          .then(res => {
            this.setState({
              goalDailyCalories: res.goalDailyCalories,
              goalDailyProtein: res.goalDailyProtein,
              goalDailyCarbohydrates: res.goalDailyCarbohydrates,
              goalDailyFat: res.goalDailyFat,
              goalDailyActivity: res.goalDailyActivity
            });
          });
      }

      calculate(){
          let compareArray = [];
          let obj = {}
          obj.name = 'Daily Activity'
          obj.goal = this.state.goalDailyActivity;
          obj.today = this.state.dailyDuration;
          if(0< this.state.dailyDuration/this.state.goalDailyActivity < 1 && this.state.goalDailyActivity !== 0){
            obj.progress = 1
          }
          else{
            obj.progress = this.state.dailyDuration/this.state.goalDailyActivity
          }
          compareArray.push(obj);

        
          obj.name = 'Daily Calories'
          obj.goal = this.state.goalDailyCalorie;
          obj.today = this.state.dailyCalories;
          if(0< this.state.dailyCalories/this.state.goalDailyCalories < 1 && this.state.goalDailyCalories !== 0){
            obj.progress = 1
          }
          else{
            obj.progress = this.state.dailyCalories/this.state.goalDailyCalories
          }
          compareArray.push(obj);

          
          obj.name = 'Daily Protein'
          obj.goal = this.state.goalDailyProtein;
          obj.today = this.state.dailyProtein;
          if(0< this.state.dailyProtein/this.state.goalDailyProtein < 1 && this.state.goalDailyProtein !== 0){
            obj.progress = 1
          }
          else{
            obj.progress =  this.state.dailyProtein/this.state.goalDailyProtein
          }
          compareArray.push(obj);

          
          obj.name = 'Daily Carbohydrates'
          obj.goal = this.state.goalDailyCarbohydrates;
          obj.today = this.state.dailyCarbohydrates;
          if(0< this.state.dailyCarbohydrates/this.state.goalDailyCarbohydrates < 1 && this.state.goalDailyCarbohydrates !== 0){
            obj.progress = 1
          }
          else{
            obj.progress =  this.state.dailyCarbohydrates/this.state.goalDailyCarbohydrates
          }
          compareArray.push(obj);

         
          obj.name = 'Daily Fat'
          obj.goal = this.state.goalDailyFat;
          obj.today = this.state.dailyFat;
          if(0< this.state.dailyFat/this.state.goalDailyFat < 1 && this.state.goalDailyFat !== 0){
            obj.progress = 1
          }
          else{
            obj.progress =  this.state.dailyFat/this.state.goalDailyFat
          }
          compareArray.push(obj);   
          console.log(compareArray)
          return compareArray;
         

      }

    //   renderItem(item){
    //     return(
    //       <View>
    //       <DataTable.Row>
    //    <DataTable.Cell style={{ justifyContent: 'flex-start'}} >{item.name}</DataTable.Cell>
    //    <DataTable.Cell >{item.today}/{item.goal}</DataTable.Cell>
      
    //  </DataTable.Row>
    //       {/* <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
    //                 <Title>item.name</Title>
    //                 <Text>{item.today}/{item.goal}</Text>
    //             </View> */}
    //             <Progress.Bar progress={item.progress} width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/>
    //            <View style={styles.spaceSmall} />
    //       </View>
    //     )
    //   }

      displayComparison(){
        let cardList = [];
        let compareArray = this.calculate()
        // console.log(this.state.activityArray, "activity")
        for (let i = 0; i < compareArray.length; i++){
        // for(const key of this.state.activityArray.keys()){
          let value = compareArray[i];
          // console.log(value, "activity info")
          // console.log(value.id, "activity id")
            cardList.push(
              <View key={i}>
          <DataTable.Row>
       <DataTable.Cell style={{ justifyContent: 'flex-start'}} >{value.name}</DataTable.Cell>
       <DataTable.Cell >{value.today}/{value.goal}</DataTable.Cell>
      
     </DataTable.Row>
          {/* <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
                    <Title>item.name</Title>
                    <Text>{item.today}/{item.goal}</Text>
                </View> */}
                {/* <Progress.Bar progress={value.progress} width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/> */}
               <View style={styles.spaceSmall} />
          </View>
              )

        }
        return cardList;
    }
      //this.state.dailyCalories/this.state.goalDailyCalories>1 ? 1: this.state.dailyCalories/this.state.goalDailyCalories
    render() {
        return (
           <View style={styles.mainContainer} contentContainerStyle={{ flexGrow: 11, justifyContent: 'center', alignItems: "center" }}>
            <Icon name="md-arrow-back-circle" size={40} color="#900" style={{ marginLeft: 20 }} />
               {/* <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
                   <Title>Daily Carbohydrates</Title>
                   <Text>{this.state.dailyCarbo}/{this.state.goalDailyCarbohydrates}</Text>
               </View>
               <Progress.Bar progress={this.state.dailyCarbo/this.state.goalDailyCarbohydrates>1 ? 1: this.state.dailyCarbo/this.state.goalDailyCarbohydrates} 
               width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/>
               <View style={styles.spaceSmall} /> */}
               {this.displayComparison()}

           </View> 
        )
    }
}

const styles = StyleSheet.create({
    mainContainer: {
        flex: 1
      },
      scrollViewContainer: {},
      container: {
        flex: 1,
        backgroundColor: '#fff',
      },
    
      spaceSmall: {
        width: 20,
        height: 10,
      },
      space: {
        width: 20,
        height: 20,
      },
});

export default ComparisonView;
