import React from 'react';
import { Avatar, Button, Card, Title, Paragraph, Chip } from 'react-native-paper';
import {StyleSheet, View, Dimensions, Modal} from "react-native";
import MealModal from "./MealModal"

class MealCard extends React.Component {
    constructor(props){
        super(props)
        this.state={
            visible: false,
        }
        
    }
    delete = () =>{
        this.props.deleteExercise(this.props.id);
    }

    showModal = () => this.setState({visible: true});
    hideModal = () => this.setState({visible: false});

    getModal(){
        if(this.state.visible){
            return(
                <MealModal visible={this.state.visible} updateData = {this.props.updateData} hideModal={this.hideModal} 
                id={this.props.id} date={this.props.date} accessToken={this.props.accessToken} name={this.props.name} 
                           />
            )
        }
    }

    randomColor = () => {
        let color = ['red', '#66CCFF', '#FFCC00', '#1C9379', '#8A7BA7'];
        let col = color[Math.floor(Math.random() * color.length)];
        return col;
    };

    foodChips(food){
        return(
            <View style = {{flex:1}}>{
                food.map((item, index) => {
                    return (
                        <View key={index} style={{margin: 5,flexWrap: 'wrap',}}>
                        <Chip key={index} mode="outlined" height={30} 
                        textStyle={{ color:'white',fontSize: 15 }} 
                        style={{ backgroundColor: this.randomColor() }} 
                        onPress={() => Alert.alert('Clicked Chip'+ item.name)}>
                        {item.name}
                        </Chip>
                    </View>
                    );
                 })}
            </View>
        )
    }

    render(){
        console.log(this.props.id, "card id")
        return(
            <Card style={styles.card}>
            <Card.Title stytle={{fontWeight: "700", fontSize: 18}} title={this.props.name} />
                 <Card.Content>
                    <View style={{flexDirection: 'column'}}>
                        <View style={styles.left}>
                            <Paragraph style={styles.para}>{this.props.date} </Paragraph>
                            <Paragraph style={styles.para}>Meal calories : {this.props. mealFoodCal}</Paragraph>
                            <Paragraph style={styles.para}>Meal proteins : {this.props. mealProteins}</Paragraph>
                            <Paragraph style={styles.para}>Meal fats : {this.props. mealFats}</Paragraph>
                            <Paragraph style={styles.para}>Meal carbohydrates : {this.props. mealCarbo}</Paragraph>
                        </View>
                        <View style={styles.spaceSmall}></View>
                        {/* {this.foodChips(this.props.foods)} */}
                    </View>
                     
                 </Card.Content>
             <Card.Actions>
             
      
                 <Button onPress={this.showModal}>Edit</Button>
                 <Button onPress={this.delete}>Delete</Button>
                 {/* <Portal> */}
                {/* <Modal visible={this.state.visible}> */}
                {/* <MealModal visible={this.state.visible}  id={this.props.id} accessToken={this.props.accessToken} /> */}
                {/* <Button color="#a1635f" style={styles.buttonInline} title="NEVERMIND" onPress={ this.hideModal} />
                 </Modal> */}
            {/* </Portal> */}
            {this.getModal()}
             </Card.Actions>
             </Card>

           
        );
    }
}

const styles = StyleSheet.create({
    card:{
        margin: 4,
        backgroundColor: "#D8BFD8",
        borderRadius: 5,
        width: Dimensions.get('window').width *0.9,
    },
    left:{
        alignItems: 'center',
        justifyContent: 'flex-start'
    },

    cal:{
        alignItems: 'center',
        justifyContent: 'flex-start',
        fontWeight: "600",
         fontSize: 16 ,
    },
    mainContainer: {
        flex: 1
      },

      para:{
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: "400",
         fontSize: 14 ,
    },
    
});

export default MealCard;
