import * as React from 'react';
import {  Portal, Provider, Card, Paragraph } from 'react-native-paper';
// import timePicker from "./timePicker";
import { StyleSheet, Text, View, Button, TextInput, Dimensions , FlatList, TouchableOpacity} from 'react-native';
import TimePicker from './TimePicker';
import Ionicons from 'react-native-vector-icons/Ionicons'
import Icon from 'react-native-vector-icons/FontAwesome5';


class Item extends React.Component {


    shouldComponentUpdate(nextProps,nextState){
        return (this.props.item != nextProps.item || this.props.select != nextProps.select);
    }

    render() {
        // 这里我就把其他布局文件省略了
        console.log('item render ')  // 从这里可以看到 每次改变item是，只刷新了指定item
        const { item } = this.props;
            return (
                <View style={styles.mainContainer}>
                <TouchableOpacity style={styles.container} onPress={this.props.onPress}>
                    {
                        item.select ?
                            <Icon name='check-square' size={10} color="#900"/>
                            :
                            <Icon name='square' size={10} color="#000"/>
                    }
                </TouchableOpacity>
                <Card style={styles.card}>
                 <Card.Title stytle={{fontWeight: "700", fontSize: 16}} title={item.name} />
                 <Card.Content style={{flexDirection: 'column'}}>
                    <Paragraph style={styles.para}>Measure: {item.measure } </Paragraph>
                    <Paragraph style={styles.para}>Calories: {item.calories} (kcal)</Paragraph>
                    <Paragraph style={styles.cal}>Protein: {item.protein} (grams)</Paragraph>
                    <Paragraph style={styles.cal}>Carbohydrates: {item.carbohydrates} (grams)</Paragraph>
                    <Paragraph style={styles.cal}>Fat: {item.fat} (grams)</Paragraph>   
                 </Card.Content>
                 </Card>
                </View>

            );
    }
}


class FoodsView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        foodArray: [],
        foodsList: [],
        accessToken: '',
        mealName: ''
        };
    }

    async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }

    getFoods(){
        fetch('https://mysqlcs639.cs.wisc.edu/foods/', {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res => res.json())
          .then(res => {
            console.log(res, "res")
            this.setState({foodArray:  res.foods})
          })
          .catch(err =>{console.log(err)});
    }

    async componentDidMount() {
        await this.getStoredData("accessToken");
        await this.getStoredData("username");
        this.getFoods();
        this.newFoodArray()
    }

    newFoodArray(){
        let newfoodsArray = this.state.foodArray;
        for(let i=0; i<newfoodsArray.length; i++){
            newfoodsArray[i].select = false;
        }
        this.setState({foodsList: newfoodsArray})
    }

    changeSelect(index, select) {
        const { foodsList } = this.state;
        foodsList[index].select = !select;
        this.setState({foodsList,})
    };

    render(){
        return(
            <View style={styles.mainContainer} >
            <Icon name="arrow-left" size={40} color="#900" style={styles.icon} onPress={() => this.props.navigation.navigate('MealsView')} />
            <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
            <Icon name="hamburger" size={40} color="#900" style={{ marginRight: 20 }} />
          <Text style={styles.bigText}>Foods</Text>
            </View>
            <View style={styles.spaceSmall}></View>

            <FlatList
                data={this.state.foodsList}
    
                keyExtractor={item => item.id}
                renderItem={({ item, index }) => (
                <Item
                    item={item}
                    select={item.select}
                    onPress={() => this.changeSelect(index, item.select)}
                />)
                }
            />
        </View>
        )
        }

}

const styles = StyleSheet.create({
    scrollView: {
        height: Dimensions.get('window').height
      },
      mainContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
      },
      
      container: {
        flex: 1,
        backgroundColor: '#fff',

      },
      
      spaceSmall: {
        width: 20,
        height: 10,
      },
      space: {
        width: 20,
        height: 20,
      },
     
      card:{
        margin: 4,
        backgroundColor: "#FAFAD2",
        borderRadius: 10,
        width: Dimensions.get('window').width *0.8,
    },

    para:{
        alignItems: 'center',
        justifyContent: 'flex-start',
        fontWeight: "400",
         fontSize: 12 ,
    },

    icon:{
        alignSelf: 'flex-end',
        position: 'absolute',
        top: 15,
        left: 15,
      }
});

export default FoodsView ;
