import React from 'react';
import moment from "moment";
import { Modal, Portal, Title, Provider, Surface } from 'react-native-paper';
import {StyleSheet, Text, View, Button, TextInput, ScrollView } from 'react-native';
import { Dimensions } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';
// import * as Progress from 'react-native-progress';
import ProgressBar from 'react-native-progress/Bar';

class ComparisonView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dailyDuration: this.props.dailyDuration,
            dailyCalories: this.props.dailyCalories,
            dailyProteins: this.props.dailyProteins,
            dailyCarbo: this.props.dailyCarbo,
            dailyFats: this.props.dailyFats,
             dailyFoodCal: this.props.dailyFoodCal,
             goalDailyCalories: 0.0,
            goalDailyProtein: 0.0,
            goalDailyCarbohydrates: 0.0,
             goalDailyFat: 0.0,
            goalDailyActivity: 0.0,
            accessToken: "",
            
        }

    }

    async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }
    

    async componentDidMount() {
        await this.getStoredData("accessToken");
        // console.log(this.state.accessToken)
        fetch('https://mysqlcs639.cs.wisc.edu/users/' + this.state.username, {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res => res.json())
          .then(res => {
            this.setState({
              goalDailyCalories: res.goalDailyCalories,
              goalDailyProtein: res.goalDailyProtein,
              goalDailyCarbohydrates: res.goalDailyCarbohydrates,
              goalDailyFat: res.goalDailyFat,
              goalDailyActivity: res.goalDailyActivity
            });
          });
      }

      calculate(){
          let compareArray = [];

      }
      //this.state.dailyCalories/this.state.goalDailyCalories>1 ? 1: this.state.dailyCalories/this.state.goalDailyCalories
    render() {
        return (
           <View style={styles.mainContainer} contentContainerStyle={{ flexGrow: 11, justifyContent: 'center', alignItems: "center" }}>
            
                <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
                    <Title>Daily Calories</Title>
                    <Text>{this.state.dailyCalories}/{this.state.goalDailyCalorie}</Text>
                </View>
                <Progress.Bar progress={this.state.dailyCalories/this.state.goalDailyCalories>1 ? 1: this.state.dailyCalories/this.state.goalDailyCalories} width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/>
          //      <View style={styles.spaceSmall} />
               {/* <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
                   <Title>Daily Carbohydrates</Title>
                   <Text>{this.state.dailyCarbo}/{this.state.goalDailyCarbohydrates}</Text>
               </View>
               <Progress.Bar progress={this.state.dailyCarbo/this.state.goalDailyCarbohydrates>1 ? 1: this.state.dailyCarbo/this.state.goalDailyCarbohydrates} 
               width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/>
               <View style={styles.spaceSmall} /> */}

           </View> 
        )
    }
}

const styles = StyleSheet.create({
    mainContainer: {
        flex: 1
      },
      scrollViewContainer: {},
      container: {
        flex: 1,
        backgroundColor: '#fff',
      },
    
      spaceSmall: {
        width: 20,
        height: 10,
      },
      space: {
        width: 20,
        height: 20,
      },
});

export default ComparisonView;
