import React from 'react';
import { Avatar, Button, Card, Title, Paragraph } from 'react-native-paper';
import {StyleSheet, View, Dimensions, Modal} from "react-native";
import ActivityModal from './ActivityModal';

class ActivityCard extends React.Component {
    constructor(props){
        super(props)
        this.state={
        visible: false,
        }
        
    }
    delete = () =>{
        this.props.deleteExercise(this.props.id);
    }

    showModal = () => this.setState({visible: true});
    hideModal = () => this.setState({visible: false});
    getModal(){
        if(this.state.visible){
            return(
                <ActivityModal visible={this.state.visible} hideModal={this.hideModal} accessToken={this.state.accessToken} addmode={true} editmode={false}/>
            )
        }
    }

    render(){
        console.log(this.props.id, "card id")
        return(
            <Card style={styles.card}>
            <Card.Title stytle={{fontWeight: "700", fontSize: 18}} title={this.props.name} />
                 <Card.Content>
                    <View style={{flexDirection: 'column'}}>
                        <View style={styles.left}>
                            <Paragraph style={styles.para}>{this.props.date} </Paragraph>
                            <Paragraph style={styles.para}>Duration: {this.props.duration} min</Paragraph>
                            <Paragraph style={styles.cal}>Calories Burnt: {this.props.calories} kcal</Paragraph>
                        </View>
                        
                            
                        
                    </View>
                     
                 </Card.Content>
             <Card.Actions>
             
      
                 <Button onPress={this.showModal}>Edit</Button>
                 <Button onPress={this.delete}>Delete</Button>
                 {this.getModal()}
                 {/* <Portal> */}
                {/* <Modal visible={this.state.visible}> */}
                {/* <ActivityModal visible={this.state.visible}  hideModal={this.hideModal} id={this.props.id} accessToken={this.props.accessToken} addmode={false} editmode={true}/> */}
                {/* <Button color="#a1635f" style={styles.buttonInline} title="NEVERMIND" onPress={ this.hideModal} />
                 </Modal> */}
            {/* </Portal> */}
             </Card.Actions>
             </Card>

           
        );
    }
}

const styles = StyleSheet.create({
    card:{
        margin: 4,
        backgroundColor: "#FAFAD2",
        borderRadius: 10,
        width: Dimensions.get('window').width *0.9,
    },
    left:{
        alignItems: 'center',
        justifyContent: 'center'
    },

    cal:{
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: "600",
         fontSize: 16 ,
    },

    para:{
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: "400",
         fontSize: 14 ,
    },
    mainContainer: {
        flex: 1
      },
    
});

export default ActivityCard;
