import React, {useState, useEffect}  from 'react';
import {View, Button, Platform, StyleSheet, Text} from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import moment from "moment";

class TimePicker extends React.Component {
    constructor(props){
        super(props);
        this.state = {
          //  date:  new Date(Date.parse(moment(this.props.date).format(
          //       'ddd MMM DD YYYY HH:mm:ss ZZ',))),//this.props.date,
          date: new Date(this.props.date),
           mode: 'date',
           show: false,
        }
        // this.getDateTime = this.getDateTime.bind(this);
     }
  
    onChange = (event, selectedDate) => {
      let currentDate = selectedDate || this.state.date;
      this.setState({show: Platform.OS === 'android' ? true : false}),
      this.setState({date: currentDate});
      this.props.getDateTime(currentDate);
    //   this.props.getDate(currentDate);
    };

    showMode = (currentMode) => {
        this.setState({show: true});
        this.setState({mode: currentMode});
    };
  
    showDatepicker = () => {
      this.showMode('date');
    };
  
    showTimepicker = () => {
      this.showMode('time');
      
    };


  componentDidMount() {
    this.setState({date:new Date(this.props.date)})
  }
   
  render(){
    const {show, date, mode} = this.state;
    console.log(this.props.date, "time picker props")
    console.log(date, "time picker state")
    // console.log(typeof Date.parse(date), "current date")
    return (
       <View>
       <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
              <Text>{moment(date).format("MMM Do YY")}</Text>
              <View style={styles.spaceHorizontal} />
              <Text>{moment(date).format('h:mm:ss a')}</Text>
        </View>
        <View style={styles.spaceSmall}></View>
      <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <Button color="#942a21" style={styles.buttonInline} onPress={this.showDatepicker} title="SET DATA" />
        <View style={styles.spaceHorizontal} />
        <Button color="#942a21" style={styles.buttonInline} onPress={this.showTimepicker} title="SET TIME" />
        
        {show && (
          <DateTimePicker
            testID="dateTimePicker"
            value={Date.parse(date)}
            mode={mode}
            is24Hour={true}
            display="default"
            onChange={this.onChange}
          />
        )}
      </View>
    
        </View> 
    );
    }
  };

  const styles = StyleSheet.create({
    buttonInline: {
        display: "flex"
      },
      spaceSmall: {
        width: 20,
        height: 10,
      },
      spaceHorizontal: {
        display: "flex",
        width: 20
      },
  });

  export default TimePicker;

