import React from 'react';
import { Avatar, Button, Card, Title, Paragraph } from 'react-native-paper';
import {StyleSheet, View, Dimensions} from "react-native";
import ActivityModal from './ActivityModal';

class ActivityCard extends React.Component {
    constructor(props){
        super(props)
        this.state={
            visible: false,
        }
        
    }
    delete = () =>{
        this.props.deleteExercise(this.props.id);
    }

    showModal = () => this.setState({visible: true});
    hideModal = () => this.setState({visible: false});

    render(){
        return(
            <Card style={styles.card}>
            <Card.Title title={this.props.name}  left={LeftContent} />
                 <Card.Content>
                    <View style={{flexDirection: 'column'}}>
                        <View style={styles.left}>
                            <Paragraph >{this.props.date} </Paragraph>
                            <Paragraph>Duration: {this.props.duration}</Paragraph>
                        </View>
                        <View> 
                            <Paragraph style={styles.cal}>{this.props.calories}</Paragraph>
                        </View>
                    </View>
                     
                 </Card.Content>
             <Card.Actions>
             
      
                 <Button onPress={this.showModal}>Edit</Button>
                 <Button onPress={this.delete}>Delete</Button>
                 {/* <Portal>
                <Modal visible={this.state.visible} onDismiss={this.hideModal} style={styles.mainContainer} contentContainerStyle={{justifyContent: 'center', alignItems: "center" }}> */}
                <ActivityModal visible={this.state.visible}  id={this.props.id} accessToken={this.props.accessToken} addmode={true} editmode={false}/>
                {/* </Modal>
            </Portal> */}
             </Card.Actions>
             </Card>

           
        );
    }
}

const styles = StyleSheet.create({
    card:{
        margin: 4,
        backgroundColor: "#f4a460",
        borderRadius: 5,
    },
    left:{
        alignItems: 'center',
        justifyContent: 'flex-start'
    },

    cal:{
        fontWeight: "700",
         fontSize: 20 
    },
    mainContainer: {
        flex: 1
      },
    
});

export default ActivityCard;
