import React from 'react';
import moment from "moment";
import { Modal, Portal, Title, Provider, Surface, DataTable, ProgressBar, Headline } from 'react-native-paper';
import {StyleSheet, Text, View, Button, TextInput, ScrollView } from 'react-native';
import { Dimensions } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';
import Icon from 'react-native-vector-icons/FontAwesome5';
// import * as Progress from 'react-native-progress';
// import ProgressBar from 'react-native-progress/Bar';

class ComparisonView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dailyDuration: this.props.route.params.dailyDuration,
            dailyCalories: this.props.route.params.dailyCalories,
            dailyProtein: this.props.route.params.dailyProtein,
            dailyCarbo: this.props.route.params.dailyCarbo,
            dailyFats: this.props.route.params.dailyFats,
             dailyFoodCal: this.props.route.params.dailyFoodCal,
             goalDailyCalories: 0.0,
            goalDailyProtein: 0.0,
            goalDailyCarbohydrates: 0.0,
             goalDailyFat: 0.0,
            goalDailyActivity: 0.0,
            accessToken: "",
            username: "",
            
        }

    }

    async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }
    

    async componentDidMount() {
      await this.getStoredData("accessToken");
        await this.getStoredData("username");
        // console.log(this.state.accessToken)
        fetch('https://mysqlcs639.cs.wisc.edu/users/' + this.state.username, {
          method: 'GET',
          headers: { 'x-access-token': this.state.accessToken }
        })
          .then(res => res.json())
          .then(res => {
            this.setState({
              goalDailyCalories: res.goalDailyCalories,
              goalDailyProtein: res.goalDailyProtein,
              goalDailyCarbohydrates: res.goalDailyCarbohydrates,
              goalDailyFat: res.goalDailyFat,
              goalDailyActivity: res.goalDailyActivity
            });

            this.setState({
              goalDailyCalories: parseFloat(this.state.goalDailyCalories),
              goalDailyProtein: parseFloat(this.state.goalDailyProtein),
              goalDailyCarbohydrates: parseFloat(this.state.goalDailyCarbohydrates),
              goalDailyFat: parseFloat(this.state.goalDailyFat),
              goalDailyActivity: parseFloat(this.state.goalDailyActivity)
            })
          });
      }

      calculate(){
        // console.log(this.state.goalDailyActivity, "goalDailyActivity")
        // console.log(this.state.goalDailyCalories, "goalDailyCalories")
        // console.log(this.state.goalDailyFat, "goalDailyFat")
        // console.log(this.state.goalDailyProtein, "goalDailyProtein")
        // console.log(this.state. dailyCarbo, " dailyCarbo")
        // console.log(this.state.dailyFats, "dailyFats")
          let compareArray = [];
          let obj = {}
          obj.name = 'Daily Activity'
          obj.goal = this.state.goalDailyActivity;
          obj.today = this.state.dailyDuration;
          console.log(typeof this.state.dailyDuration)
          console.log( this.state.dailyDuration/this.state.goalDailyActivity)
          if(this.state.goalDailyActivity !== 0 &&  this.state.dailyDuration/this.state.goalDailyActivity < 1){
            obj.progress = this.state.dailyDuration/this.state.goalDailyActivity
            console.log("asdfasfdsf")
          }
          else{
            obj.progress = 1
          }
          compareArray.push(obj);

          let obj1 = {}
          obj1.name = 'Daily Calories'
          // console.log(this.state.goalDailyCalories, "goalDailyCalories")
          // console.log(typeof this.state.dailyCalories)
          obj1.goal = this.state.goalDailyCalories;
          obj1.today = this.state.dailyCalories;
          if(this.state.goalDailyCalories !== 0 && this.state.dailyCalories/this.state.goalDailyCalories < 1){
            obj1.progress = this.state.dailyCalories/this.state.goalDailyCalories
          }
          else{
            obj1.progress = 1
          }
          compareArray.push(obj1);

          let obj2 = {}
          obj2.name = 'Daily Protein'
          obj2.goal = this.state.goalDailyProtein;
          obj2.today = this.state.dailyProtein;
          if(this.state.goalDailyProtein !== 0 && this.state.dailyProtein/this.state.goalDailyProtein < 1){
            obj2.progress = this.state.dailyProtein/this.state.goalDailyProtein
          }
          else{
            obj2.progress =  1
          }
          compareArray.push(obj2);

          let obj3 = {}
          obj3.name = 'Daily Carbohydrates'
          obj3.goal = this.state.goalDailyCarbohydrates;
          obj3.today = this.state.dailyCarbo;
          if(this.state.goalDailyCarbohydrates !== 0 && this.state.dailyCarbo/this.state.goalDailyCarbohydrates < 1 ){
            obj3.progress = this.state.dailyCarbos/this.state.goalDailyCarbohydrates
          }
          else{
            obj3.progress =  1
          }
          compareArray.push(obj3);

          let obj4 = {}
          obj4.name = 'Daily Fats'
          obj4.goal = this.state.goalDailyFat;
          obj4.today = this.state.dailyFats;
          if(this.state.goalDailyFat !== 0 &&  this.state.dailyFats/this.state.goalDailyFat < 1){
            obj4.progress = this.state.dailyFats/this.state.goalDailyFat
          }
          else{
            obj4.progress =  1
          }
          compareArray.push(obj4);   
          console.log(compareArray)
          return compareArray;
         

      }

    //   renderItem(item){
    //     return(
    //       <View>
    //       <DataTable.Row>
    //    <DataTable.Cell style={{ justifyContent: 'flex-start'}} >{item.name}</DataTable.Cell>
    //    <DataTable.Cell >{item.today}/{item.goal}</DataTable.Cell>
      
    //  </DataTable.Row>
    //       {/* <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
    //                 <Title>item.name</Title>
    //                 <Text>{item.today}/{item.goal}</Text>
    //             </View> */}
    //             <Progress.Bar progress={item.progress} width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/>
    //            <View style={styles.spaceSmall} />
    //       </View>
    //     )
    //   }

      displayComparison(){
        let cardList = [];
        let compareArray = this.calculate()
        // console.log(this.state.activityArray, "activity")
        for (let i = 0; i < compareArray.length; i++){
        // for(const key of this.state.activityArray.keys()){
          let value = compareArray[i];
          // console.log(value, "activity info")
          // console.log(value.id, "activity id")
            cardList.push(
              <View key={i}>
          {/* <DataTable.Row>
       <DataTable.Cell  style={{ justifyContent: 'flex-start',   fontWeight: "700", fontSize: 16 }}>{value.name}</DataTable.Cell>
       <DataTable.Cell style={{ justifyContent: 'flex-start', marginLeft: 20,  fontWeight: "700", fontSize: 16 }}>{value.today}/{value.goal}</DataTable.Cell>
      
     </DataTable.Row> */}
          <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
                    <Text style={{ flex:1.8,justifyContent: 'flex-start',   fontWeight: "700", fontSize: 16 }}>{value.name}</Text>
                    <Text style={{ flex:1, justifyContent: 'flex-end',  marginLeft: 60,fontWeight: "700", fontSize: 16 }}>{value.today}/{value.goal}</Text>
                </View>
                {/* <Progress.Bar progress={value.progress} width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/> */}
               <ProgressBar style={styles.bar} progress={value.progress} color={'#900'}/>
               <View style={styles.spaceSmall} />
          </View>
              )

        }
        return cardList;
    }
      //this.state.dailyCalories/this.state.goalDailyCalories>1 ? 1: this.state.dailyCalories/this.state.goalDailyCalories
    render() {
        return (
           <View style={styles.mainContainer} >
            <Icon name="arrow-left" size={40} color="#900" style={styles.icon} onPress={() => this.props.navigation.goBack()} />
            <Headline>Degree of Completion</Headline>
            <View style={styles.spaceSmall} />
            <View style={styles.spaceSmall} />
               {/* <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
                   <Title>Daily Carbohydrates</Title>
                   <Text>{this.state.dailyCarbo}/{this.state.goalDailyCarbohydrates}</Text>
               </View>
               <Progress.Bar progress={this.state.dailyCarbo/this.state.goalDailyCarbohydrates>1 ? 1: this.state.dailyCarbo/this.state.goalDailyCarbohydrates} 
               width={200} color={'tomato'} borderWidth={'3'} borderColor={'tomato'}  height={10} borderRadius={5}/>
               <View style={styles.spaceSmall} /> */}
               {this.displayComparison()}

           </View> 
        )
    }
}

const styles = StyleSheet.create({
    mainContainer: {
        flex: 1,
        justifyContent: 'center', alignItems: "center" 
      },
      scrollViewContainer: {},
      container: {
        flex: 1,
        backgroundColor: '#fff',
      },
    
      spaceSmall: {
        width: 20,
        height: 20,
      },
      space: {
        width: 20,
        height: 30,
      },

      bar:{
        height: 20,
        width: Dimensions.get('window').width *0.9,
        borderRadius: 10,
      },
      icon:{
        alignSelf: 'flex-end',
        position: 'absolute',
        top: 25,
        left: 15,
      }
});

export default ComparisonView;
