import * as React from 'react';
import {  Portal, Provider } from 'react-native-paper';
// import timePicker from "./timePicker";
import { StyleSheet, Text, View, Button, TextInput, Dimensions , Modal,} from 'react-native';
import TimePicker from './TimePicker';
import AsyncStorage from '@react-native-community/async-storage';
// import { Overlay } from 'react-native-elements';

class AddMeal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          name: "",
          date:  (new Date()).toISOString(),
          id: '',
          accessToken: '',
          modalVisible: this.props.visible,
        }
      }

      async getStoredData(key){
        try{
            let value = await AsyncStorage.getItem(key);
            this.setState({[key]: value});
         } catch (error) {
            console.log(error);
         }
    }

     

     handleAddMeal() {
        fetch('https://mysqlcs639.cs.wisc.edu/meals/', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'x-access-token': this.props.accessToken
            },
            body: JSON.stringify({
              name: this.state.name,
                date: this.state.date,
            })
          })
        .then(res => res.json())
        .then(res => {
        //     console.log(this.state.name, "name")
        //   console.log(res, 'add res')
            this.props.updateData();
            alert("One meal is added!");
            this.props.hideModal()
        })
        .catch(err => {
            alert("Something went wrong! Verify you have filled out the fields correctly.");
        });
      
    }

    getDateTime(date){
        this.setState({
          date: date,
        })
      }
      // clsoe(){
      //   this.props.hideModal()
      // }

    //   getButton(){
    //     if(this.props.editmode){
    //       return(
    //       <Button color="#942a21" style={styles.buttonInline} title="SAVE EXERCISE"  onPress={() => this.handleSaveExercise()} />
    //       )
    //     }
    //     else{
    //       return(
    //         <Button color="#942a21" style={styles.buttonInline} title="SAVE EXERCISE"  onPress={() => this.handleAddExercise()} />
    //       )
    //     }
        
    //   }

      
      render(){
       
        return (
    <Provider>
      <View style={styles.mainContainer}>
        <Modal  visible= {this.props.visible}  >
        {/* <View style={styles.space} /> */}
          <Text style={styles.bigText}>Meal Details</Text>
          <View style={styles.space} />
        <View>
        <Text style={{ textAlignVertical: "center", fontWeight: "700" }}>Meal Name</Text>
        </View>
        <TextInput style={styles.input}
          underlineColorAndroid="transparent"
        //   placeholder="Badger"
          placeholderTextColor="#d9bebd"
          onChangeText={(name) => this.setState({ name: name })}
          defaultValue={this.state.name}
          autoCapitalize="none" />
       
        <View style={styles.space} />
        <View>
          <Text style={{ textAlignVertical: "center", fontWeight: "700" }}>Meal Data and Time</Text>    
        </View>
        <View style={styles.spaceSmall}></View>
        <TimePicker  date={this.state.date}  getDateTime={(date) =>this.getDateTime(date)}/>
        {/* getDateTime={(date) => this.getDateTime(date)} */}
        <View style={styles.space} />
        <View>
        <Text style={{ textAlignVertical: "center", fontWeight: "400" }}>Looks good! Ready to save your work?</Text>
        </View>
          
        <View style={styles.space} />  
        <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <Button color="#942a21" style={styles.buttonInline} title="SAVE MEAL"  onPress={() => this.handleAddMeal()} />
        <View style={styles.spaceHorizontal} />
          <Button color="#a1635f" style={styles.buttonInline} title="NEVER MIND" onPress={() => this.props.hideModal()} />
        </View>
        </Modal>
      </View>
    </Provider> );}
  
};

const styles = StyleSheet.create({
    scrollView: {
      height: Dimensions.get('window').height
    },
    mainContainer: {
      backgroundColor: 'white', 
      padding: 5,
      // height: Dimensions.get('window').height -20,
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginTop: 22,
      marginLeft: 22,
      elevation:100,
      // width: Dimensions.get('window').width*0.9,
      // height: Dimensions.get('window').height*0.8,
    },
    scrollViewContainer: {},
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    bigText: {
      fontSize: 20,
      fontWeight: "700",
      marginBottom: 5
    },
    spaceSmall: {
      width: 20,
      height: 10,
    },
    space: {
      width: 20,
      height: 20,
    },
    spaceHorizontal: {
      display: "flex",
      width: 20
    },
    buttonInline: {
      display: "flex"
    },
    input: {
      width: 200,
      padding: 10,
      margin: 5,
      height: 40,
      borderColor: '#c9392c',
      borderWidth: 1
    },
    inputInline: {
      flexDirection: "row",
      display: "flex",
      width: 200,
      padding: 10,
      margin: 5,
      height: 40,
      borderColor: '#c9392c',
      borderWidth: 1
    }
  });
  
  export default AddMeal;
